(()=>{"use strict";(()=>{const e=document.querySelector(".modal-callback"),l=document.querySelector(".modal-overlay");document.querySelectorAll(".callback-btn").forEach((t=>{t.addEventListener("click",(()=>{e.style.display="block",l.style.display="block"}))})),document.addEventListener("click",(t=>{(t.target.classList.contains("modal-overlay")||t.target.closest(".modal-close"))&&(e.style.display="none",l.style.display="none")}))})()})();